function openService(serviceContent) {
    // HTML structure for the new window content
    const content = `
        <html>
            <head>
                <title>${serviceContent.title}</title>
                <style>
                    body {
                        font-family: Arial, sans-serif;
                        padding: 20px;
                        text-align: center;
                        background-color: #f5f5f5;
                    }
                    h1 {
                        color: #333;
                    }
                    img {
                        max-width: 100%;
                        height: auto;
                        margin: 20px 0;
                    }
                    .back-arrow {
                        font-size: 24px;
                        color: #333;
                        cursor: pointer;
                        margin-top: 20px;
                    }
                    .back-arrow:hover {
                        text-decoration: underline;
                    }
                </style>
            </head>
            <body>
                <h1>${serviceContent.title}</h1>
                <img src="${serviceContent.image}" alt="${serviceContent.title}">
                <p>${serviceContent.description || ''}</p>

                <!-- Back Button -->
                <span class="back-arrow" onclick="goBack()">&#8592; Back</span>

                <script>
                    function goBack() {
                        window.history.back();
                    }
                </script>
            </body>
        </html>
  
        
  `;
    // Push a new state to the history to enable back button functionality
    history.pushState({ page: serviceContent.title }, serviceContent.title, "#" + serviceContent.title.toLowerCase().replace(/\s+/g, '-'));

    // Open the content in the current tab
    document.open();
    document.write(content);
    document.close();

    // Handle back button functionality by listening for the 'popstate' event
    window.addEventListener("popstate", function (event) {
        if (event.state === null || event.state.page !== serviceContent.title) {
            // When the back button is pressed, return to the previous page or restore the original content
            location.reload(); // Customize this based on how you want to handle the back navigation
        }
    });
}

